INSERT INTO `config_info` (`id`, `data_id`, `group_id`, `content`, `md5`, `gmt_create`, `gmt_modified`, `src_user`, `src_ip`, `app_name`, `tenant_id`, `c_desc`, `c_use`, `effect`, `type`, `c_schema`, `encrypted_data_key`) VALUES (216, 'yyy-gateway-prod.yml', 'DEFAULT_GROUP', 'spring:\n  redis:\n    host: yyy-redis\n    port: 6379\n    database: 0\n    timeout: 10000\n    #自定义属性\n    expire: 60000\n  cache:\n    type: redis\n    #设置缓存过期时间  防止缓存雪崩：大量key同时过期\n    redis:\n      time-to-live: 3600000\n      #指定默认前缀，如果此处我们指定了前缀则使用我们指定的前缀，推荐此处不指定前缀\n      #spring.cache.redis.key-prefix=CACHE_\n      #是否开始前缀，建议开启\n      use-key-prefix: true\n      #是否缓存空值，防止缓存穿透\n      cache-null-values: true\n  cloud:\n    gateway:\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: yyy-auth\n        # 路由的目标地址 lb就是负载均衡，后面跟服务名称\n          uri: lb://yyy-auth\n          predicates:  # 路由断言，也就是判断请求是否符合路由规则的条件\n          #当请求路径与 /auth/** 匹配时，该请求才能被转发到 http://localhost:9200 上(对应yyy-auth服务的端口9200)。\n            - Path=/auth/**\n          filters:\n            # 验证码处理\n            - CacheRequestFilter\n            - ValidateCodeFilter\n            #StripPrefix=1配置，表示网关转发到业务模块时候会自动截取前缀。\n            - StripPrefix=1\n        # 代码生成\n        - id: yyy-gen\n          uri: lb://yyy-gen\n          predicates:\n            - Path=/code/**\n          filters:\n            - StripPrefix=1\n        # 定时任务\n        - id: yyy-job\n          uri: lb://yyy-job\n          predicates:\n            - Path=/schedule/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: yyy-system\n          uri: lb://yyy-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n            #黑名单配置\n            # - name: BlackListUrlFilter\n            #   args:\n            #     blacklistUrl:\n            #     - /user/list\n        # mq模块\n        - id: yyy-rabbit\n          uri: lb://yyy-rabbit\n          predicates:\n            - Path=/rabbit/**\n          filters:\n            - StripPrefix=1\n        # 测试模块\n        - id: yyy-test\n          uri: lb://yyy-test\n          predicates:\n            - Path=/test/**\n          filters:\n            - StripPrefix=1\n\n# 安全配置\nsecurity:\n  # 验证码\n  captcha:\n    enabled: false\n    type: math\n  # 防止XSS攻击\n  xss:\n    enabled: true\n    excludeUrls:\n      - /system/notice\n  # 不校验白名单\n  ignore:\n    whites:\n      - /auth/logout\n      - /auth/login\n      - /auth/register\n      - /*/v2/api-docs\n      - /csrf\n', '75de7c0e5897bc7bddc7d7e479d1a317', '2023-07-15 01:53:19', '2024-01-02 02:07:10', 'nacos', '0:0:0:0:0:0:0:1', '', '1015fa17-66f8-45dd-b604-9c2277edfe65', '网关模块', '', '', 'yaml', '', NULL);
